mapper.select:
SELECT * FROM crm_customers WHERE EXISTS (select 1 FROM crm_businesstype_user WHERE crm_customers.btid = crm_businesstype_user.btid AND crm_businesstype_user.uid = #{uid}
AND ( name LIKE concat('%',#{value},'%') OR address LIKE concat('%',#{value},'%') OR post LIKE concat('%',#{value},'%') OR nationality LIKE concat('%',#{value},'%')) )
ORDER BY CONVERT(${sort} USING gbk) ${dir}

SELECT c.* FROM v_crm_customer_firm c WHERE EXISTS ( SELECT 1 FROM crm_businesstype_user bt WHERE c.btid = bt.btid AND bt.uid = #{uid}) AND NOT EXISTS ( SELECT 1 FROM mm_receipt d WHERE d.cid=c.id AND mid = #{mid})
AND ( name LIKE concat('%',#{value},'%') OR fname LIKE concat('%',#{value},'%') OR post LIKE concat('%',#{value},'%'))
ORDER BY CONVERT(${sort} USING gbk) ${dir}
