mapper.select:
SELECT * FROM v_mm_meeting_receipt WHERE 1 = 1
AND name LIKE concat('%',#{value},'%')
ORDER BY CONVERT(${sort} USING gbk) ${dir}

SELECT * FROM v_mm_receipt_info WHERE mid = #{mid}
AND ( name LIKE concat('%',#{value},'%') OR uname LIKE concat('%',#{value},'%') OR fname LIKE concat('%',#{value},'%') OR post LIKE concat('%',#{value},'%'))
ORDER BY CONVERT(${sort} USING gbk) ${dir}
