mapper.select:
SELECT *
FROM qzgz_canteen
WHERE 1=1

    AND state = #{state}

ORDER BY CONVERT(name USING gbk)
LIMIT #{startRow},#{number}

mapper.update:
UPDATE qzgz_canteen SET
    name = #{name},
    type = #{type},
    modifier = #{modifier}
WHERE id = #{id}

UPDATE qzgz_canteen SET
    state = #{state},
    modifier = #{modifier}
WHERE id = #{id}

mapper.select:
SELECT * FROM qzgz_canteen WHERE (name like concat('%',#{name},'%') or type like concat('%',#{name},'%'))
AND state = #{state}
ORDER BY CONVERT(name USING gbk)
