mapper.update:
update report_info
set name = #{name}, reportid = #{reportid}, directoryid = #{directoryid}, type = #{type}
where id = #{id}

mapper.select:
select
i.id, i.name, i.reportid, i.directoryid, i.type, i.creater, i.created, i.modifier, i.modified
from report_info i,report_acc_info ai where i.reportid=ai.reportid and ai.accountid=#{accountid,jdbcType=VARCHAR}

select
i.id, i.name, i.reportid, i.directoryid, i.type, i.creater, i.created, i.modifier, i.modified
from report_info i,report_permission p where i.reportid=p.reportid and p.empnum=#{empnum,jdbcType=VARCHAR}
