mapper.update:
update sys_dict
set code = #{code}, name = #{name}, description = #{description}, modifier = #{modifier}
where id = #{id}

update sys_dict
set deleteFlag = 1
where id = #{id}

mapper.select:
select count(1)
from sys_dict where deleteFlag = 0

    and code=#{code,jdbcType=VARCHAR}


    and id !=#{id,jdbcType=INTEGER}

